import { useState } from 'react'

const Button = ({ onClick, text }) => <button onClick={onClick} type="submit"> {text} </button>

const ShowPeople = ({p}) => p.map(person=><p key={person.name}>{person.name} {person.number}</p> )

const ShowFilteredPeople = ({p}) => filterIndex!== -1 ? <div>{p[filterIndex].name} {p[filterIndex].number}</div> : <div>.</div>;

const CheckExistingNames = (persons, newName) => persons.includes(newName);

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456'},
    { name: 'Ada Lovelace', number: '39-44-5323523'},
    { name: 'Dan Abramov', number: '12-43-234345'},
    { name: 'Mary Poppendieck', number: '39-23-6423122'} ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterIndex, setFilter] = useState(-1)

  const handleNameChange  = (e) => setNewName(e.target.value)
  const handleNumberChange  = (e) => setNewNumber(e.target.value)
  const handleFilterChange  = (e) => {
    let nameSearch = e.target.value;
    console.log(nameSearch)
    const contactIndex = persons.map(person=> person.name).indexOf(nameSearch);
    console.log(contactIndex);
    if(contactIndex!== -1) {
      setFilter(contactIndex)
    }

  }
  const handleSaveContact = (e) =>{
    e.preventDefault()
    const nameObject = {
      name: newName,
      number: newNumber
    }

    if(newName !== '' && newNumber !== ''){
      let nameTaken = CheckExistingNames(persons.map(person=> person.name ), newName);

      if(!nameTaken){
        setPersons(persons.concat(nameObject))
        setNewName('')
      }
      else{
        alert(`${newName} is already added to phonebook`)
      }
    }
    else{
      alert(`Fill out name and number`)
    }
  }
  console.log(filterIndex);

  return (
    <>
      <h2> Phonebook </h2>
      <div>filter shown with <input onChange={handleFilterChange}/> </div>
      <div><ShowFilteredPeople p = {persons}/></div>
      <h2> add a new </h2>
      <form>
        <div>name: <input onChange={handleNameChange}/></div>
        <div>number: <input onChange={handleNumberChange}/></div>
        <div> <Button onClick={handleSaveContact} text={'add'} /> </div>
      </form>
      <h2>Numbers</h2>
      <ShowPeople p = {persons}/>
    </>
  )
}

export default App
