import { useState } from 'react'

const Button = ({ onClick, text }) => <button onClick={onClick}> {text} </button>
const ShowPeople = ({p}) =>{
  return(
    p.map(person=><p key={person.name}>{person.name}</p> )
  )
}
const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')

  const handleNameChange  = (e) => {
    if(e.target.value !== '')
      setNewName(e.target.value)
  }
  const handleSaveName = (e) =>{
    e.preventDefault()
    const nameObject = {
      name: newName
    }
    if(newName!==''){
      setPersons(persons.concat(nameObject))
      setNewName('')
    }
  }
  console.log(persons);
  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          name: <input onChange={handleNameChange}/>
        </div>
        <div>
          <Button onClick={handleSaveName} text={'add'} />
        </div>
      </form>
      <h2>Numbers</h2>
      debug: <ShowPeople p = {persons}/>
    </div>

  )
}

export default App
