import { useState } from 'react'

const Button = ({ onClick, text }) => <button onClick={onClick}> {text} </button>

const ShowPeople = ({p}) =>{
  return(
    p.map(person=><p key={person.name}>{person.name}</p> )
  )
}
const CheckExistingNames = (persons, newName) => {
  console.log("this are the names ", persons, "   this is the new name ", newName);
  return true;
}
const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')

  const handleNameChange  = (e) => {
    setNewName(e.target.value)
  }
  const handleSaveName = (e) =>{
    const nameObject = {
      name: newName
    }
    if(newName!==''){
      let nameIsTaken = CheckExistingNames(persons.map(person=> person.name ), {newName})
      console.log(persons.map(person=> person.name ));
      if(nameIsTaken){
        setPersons(persons.concat(nameObject))
        setNewName('')
      }
    }
  }
  console.log("this are the persons", persons);
  return (
    <div>
      <h2>Phonebook</h2>
        <div>
          name: <input onChange={handleNameChange}/>
        </div>
        <div>
          <Button onClick={handleSaveName} text={'add'} />
        </div>
      <h2>Numbers</h2>
      debug: <ShowPeople p = {persons}/>
    </div>

  )
}

export default App
